// src/pages/MenuManagementPage.js
import { addDoc, collection, deleteDoc, doc, onSnapshot, updateDoc } from "firebase/firestore";
import { useEffect, useMemo, useState } from "react";
import { db } from "../firebase";

export default function MenuManagementPage() {
  const [menu, setMenu] = useState([]);
  const [openSections, setOpenSections] = useState({});
  const [newItem, setNewItem] = useState({ name: "", price: "", category: "", inStock: true });
  const [search, setSearch] = useState("");

  // Fetch menu items live
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "menuItems"), (snap) => {
      setMenu(snap.docs.map(d => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, []);

  //search feature
    const filteredMenu = useMemo(() => {
    const term = search.toLowerCase();
    return menu.filter(
        item =>
        item.name.toLowerCase().includes(term) ||
        item.category.toLowerCase().includes(term)
    );
    }, [menu, search]);

  // Group menu by category
  const groupedMenu = useMemo(() => {
    const groups = {};
    menu.forEach(item => {
      if (!groups[item.category]) groups[item.category] = [];
      groups[item.category].push(item);
    });
    return groups;
  }, [menu]);

  const toggleSection = (name) => {
    setOpenSections(prev => ({ ...prev, [name]: !prev[name] }));
  };

  // CRUD operations
  const addItem = async () => {
    if (!newItem.name || !newItem.price || !newItem.category) return alert("Fill all fields!");
    await addDoc(collection(db, "menuItems"), {
      ...newItem,
      price: Number(newItem.price),
      inStock: true
    });
    setNewItem({ name: "", price: "", category: "", inStock: true });
  };

  const updatePrice = async (id, price) => {
    await updateDoc(doc(db, "menuItems", id), { price: Number(price) });
  };

  const toggleStock = async (id, current) => {
    await updateDoc(doc(db, "menuItems", id), { inStock: !current });
  };

  const deleteItem = async (id) => {
    if (window.confirm("Delete this item?")) {
      await deleteDoc(doc(db, "menuItems", id));
    }
  };

  return (
    <div className="container">
      <h2>Menu Management</h2>

      {/* Add new item */}
      <div className="card">
        <h3>Add New Item</h3>
        <input
          className="input"
          placeholder="Category"
          value={newItem.category}
          onChange={(e) => setNewItem({ ...newItem, category: e.target.value })}
        />
        <input
          className="input"
          placeholder="Item Name"
          value={newItem.name}
          onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
        />
        <input
          type="number"
          className="input"
          placeholder="Price"
          value={newItem.price}
          onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
        />
        <button className="btn" onClick={addItem}>Add Item</button>
      </div>

      <div style={{ marginBottom: "10px" }}>
            <input
                type="text"
                className="input"
                placeholder="Search menu..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />
      </div>

      {Object.entries(groupedMenu).map(([category, items]) => (
    <div key={category} style={{ marginTop: "16px" }}>
      <h4 style={{ background: "#f4f4f4", padding: "6px", borderRadius: "4px" }}>
        {category}
      </h4>
      {items.map(item => (
        <div
          key={item.id}
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            padding: "6px 0",
            borderBottom: "1px solid #ddd"
          }}
        >
          <div>
            <strong>{item.name}</strong> — ₹{item.price}
            <span style={{ marginLeft: "8px", fontSize: "0.9em", color: item.inStock ? "green" : "red" }}>
              {item.inStock ? "In Stock" : "Out of Stock"}
            </span>
          </div>
          <div>
            <button className="btn secondary" onClick={() => toggleStock(item)}>
              Toggle Stock
            </button>
            <button className="btn secondary" onClick={() => editItem(item)}>
              Edit
            </button>
            <button className="btn danger" onClick={() => deleteItem(item.id)}>
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  ))}


      {/* Existing items by category */}
      {Object.keys(groupedMenu).map(category => (
        <div key={category} style={{ marginTop: 16 }}>
          <h4
            style={{
              cursor: "pointer",
              padding: "8px",
              background: "#f4f4f4",
              borderRadius: 4
            }}
            onClick={() => toggleSection(category)}
          >
            {category} {openSections[category] ? "▲" : "▼"}
          </h4>

          {openSections[category] && (
            <div style={{ marginTop: 8 }}>
              {groupedMenu[category].map(item => (
                <div
                  key={item.id}
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    padding: "6px 0",
                    borderBottom: "1px solid #ddd"
                  }}
                >
                  <span>{item.name}</span>
                  <input
                    type="number"
                    defaultValue={item.price}
                    style={{ width: 60, marginRight: 8 }}
                    onBlur={(e) => updatePrice(item.id, e.target.value)}
                  />
                  <button className="btn secondary" onClick={() => toggleStock(item.id, item.inStock)}>
                    {item.inStock ? "Mark Out of stock" : "Mark In stock"}
                  </button>
                  <button className="btn secondary" onClick={() => deleteItem(item.id)}>Delete</button>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
