// src/pages/MenuPage.js
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { useMemo, useState } from "react";
import { db } from "../firebase";

// Menu structured into categories and sub-items
const MENU_SECTIONS = [
  {
    name: "Tea",
    items: [
      { id: "special-tea", name: "Special Tea", price: 30 },
      { id: "allam-tea", name: "Allam Tea", price: 35 },
      { id: "green-tea", name: "Green Tea", price: 40 },
      { id: "lemon-tea", name: "Lemon Tea", price: 35 },
    ]
  },
  {
    name: "Coffee",
    items: [
      { id: "latte", name: "Latte", price: 110 },
      { id: "cappuccino", name: "Cappuccino", price: 130 },
      { id: "espresso", name: "Espresso", price: 90 },
    ]
  },
  {
    name: "Snacks",
    items: [
      { id: "sandwich", name: "Veg Sandwich", price: 150 },
      { id: "brownie", name: "Brownie", price: 80 },
    ]
  }
];

export default function MenuPage(){
  const [customer, setCustomer] = useState("");
  const [table, setTable] = useState("");
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    setCart(prev => {
      const found = prev.find(p => p.id === item.id);
      if(found){
        return prev.map(p => p.id === item.id ? { ...p, qty: p.qty + 1 } : p);
      }
      return [...prev, { ...item, qty: 1 }];
    });
  };

  const decQty = (id) => {
    setCart(prev => prev.map(p => p.id === id ? { ...p, qty: p.qty - 1 } : p).filter(p => p.qty > 0));
  };

  const removeItem = (id) => setCart(prev => prev.filter(p => p.id !== id));
  const clearCart = () => setCart([]);

  const total = useMemo(()=> cart.reduce((s, i) => s + i.price * i.qty, 0), [cart]);

  const placeOrder = async () => {
    if(!customer.trim()) return alert("Please enter your name.");
    if(cart.length === 0) return alert("Cart is empty.");

    const order = {
      customer: customer.trim(),
      table: table.trim() || "",
      items: cart.map(i => ({ id: i.id, name: i.name, price: i.price, qty: i.qty })),
      total,
      status: "Pending",
      createdAt: serverTimestamp()
    };

    try {
      const docRef = await addDoc(collection(db, "orders"), order);
      alert(`âœ… Order placed! Order ID: ${docRef.id}`);
      setCustomer("");
      setTable("");
      clearCart();
    } catch (err) {
      console.error("Failed to place order:", err);
      alert("Failed to place order. Check console for details.");
    }
  };

  return (
    <div>
      <div className="card">
        <h2>Place Order</h2>
        <div style={{ display: "flex", gap: 10, marginTop: 8 }}>
          <input className="input" placeholder="Your name" value={customer} onChange={(e) => setCustomer(e.target.value)} />
          <input className="input" placeholder="Table no. or phone (optional)" value={table} onChange={(e) => setTable(e.target.value)} />
        </div>
      </div>

      <div className="card">
        <h3>Menu</h3>
        {MENU_SECTIONS.map(section => (
          <div key={section.name} style={{ marginTop: 16 }}>
            <h4>{section.name}</h4>
            <div className="menu-grid" style={{ marginTop: 8 }}>
              {section.items.map(item => (
                <div key={item.id} className="menu-item">
                  <div>
                    <h4>{item.name}</h4>
                    <p>â‚¹{item.price}</p>
                  </div>
                  <div style={{ marginTop: "auto" }}>
                    <button className="btn" onClick={() => addToCart(item)}>Add</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      <div className="card">
        <h3>Your Cart</h3>
        {cart.length === 0 ? <p className="small">Cart is empty</p> : (
          <>
            <div className="cart">
              {cart.map(c => (
                <div key={c.id} style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 8 }}>
                  <div>
                    <strong>{c.name}</strong>
                    <div className="small">â‚¹{c.price} Ã— {c.qty}</div>
                  </div>
                  <div className="row">
                    <button className="btn secondary" onClick={() => decQty(c.id)}>-</button>
                    <button className="btn secondary" onClick={() => addToCart({ id: c.id, name: c.name, price: c.price })}>+</button>
                    <button className="btn secondary" onClick={() => removeItem(c.id)}>Remove</button>
                  </div>
                </div>
              ))}
            </div>

            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginTop: 12 }}>
              <div><strong>Total: â‚¹{total}</strong></div>
              <div>
                <button className="btn" onClick={placeOrder}>Place Order</button>
                <button className="btn secondary" style={{ marginLeft: 8 }} onClick={clearCart}>Clear</button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
